export default function clickReducer(
    state = 0, action
) {
    if (action.type === 'boardClick') {
        if (value === 'X') {
            state[action.x][action.y] = '';
        } else {
            state[action.x][action.y] = 'X';
        }
        return [...state];
    }

}


